import google from './images/google.png';
import twitter from './images/twitter.png';
import './App.css';
import axios from 'axios';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Thx from './Thx.js';

function App() {
  axios.get('https://accounts.google.com/o/oauth2/auth', {
  headers: {
    'Access-Control-Allow-Origin': '*'
  }
})
  // function to redirect to google auth
  const googleAuth = () => {
    window.location.href = 'http://127.0.0.1:5000';
  }
  const twitterAuth = () => {
    // go to Thx.js
    <link to='/thx' />
  }
  return (
    <Router>
      <Routes>
          <Route exac path='/' component={App} />
          <Route path='/thx' component={Thx} />
      <div className="App">
        <div className='box'>
            <h1 className='header'>Authentication Service</h1>
          <div className='Auth_button'onClick={googleAuth}>
            <img src={google} alt='google' className='google'/>
            <div className='buttontittle'>Sign in with Google</div>
          </div>
          <div className='Auth_button' onClick={twitterAuth}>
          <img src={twitter} alt='twitter' className='twitter'/>
            <div className='buttontittle'>Sign in with Twitter</div>
          </div>
        </div>
      </div>
      </Routes>
    </Router>
  );
}

export default App;
